SELECT user_id AS userId, u.organization_id AS organizationId, o.organization_name AS organizationName, login_name AS loginName, user_name AS userName, real_name AS realName, sex, age, pic_img AS picImg, u.status, email, telephone, last_login_time AS lastLoginTime, last_login_ip AS lastLoginIp, u.create_time AS createTime, u.create_by AS createBy, u.update_time AS updateTime, u.update_by AS updateBy FROM cms_user u LEFT JOIN cms_organization o ON u.organization_id = o.organization_id
1=1
AND telephone = #{telephone}
AND email = #{email}
AND u.organization_id = #{organizationId}

SELECT user_id AS userId, u.organization_id AS organizationId, o.organization_name AS organizationName, login_name AS loginName, user_name AS userName, real_name AS realName, sex, age, pic_img AS picImg, u.status, email, telephone, last_login_time AS lastLoginTime, last_login_ip AS lastLoginIp, u.create_time AS createTime, u.create_by AS createBy, u.update_time AS updateTime, u.update_by AS updateBy FROM cms_user u LEFT JOIN cms_organization o ON u.organization_id = o.organization_id
u.organization_id = #{organizationId}
AND ( user_name LIKE '%' #{search} '%' OR real_name LIKE '%' #{search} '%' OR telephone LIKE '%' #{search} '%' OR email LIKE '%' #{search} '%' OR o.organization_name LIKE '%' #{search} '%' )
ORDER BY ${pageInfo.sort} ${pageInfo.order}
