mapper.select:
	SELECT
        title, 
        href, 
        pic_img AS picImg, 
        content
	FROM
		os_advert_detail
	WHERE
		advert_id = #{advertId}	
	AND status = #{status}
	AND begin_time <= #{date}
	AND end_time >= #{date}
	ORDER BY 
		sort
	LIMIT ${defultNumber}

SELECT advert_detail_id AS advertDetailId, advert_id AS advertId, title, sort, href, status, pic_img AS picImg, create_time AS createTime, create_by AS createBy, update_time AS updateTime, update_by AS updateBy, remarks, content, begin_time AS beginTime, end_time AS endTime FROM os_advert_detail
advert_id = #{advertId}
AND ( title LIKE '%' #{search} '%' OR href LIKE '%' #{search} '%' OR name LIKE '%' #{search} '%' OR remarks LIKE '%' #{search} '%' OR create_by LIKE '%' #{search} '%' OR update_by LIKE '%' #{search} '%' OR content LIKE '%' #{search} '%' )
ORDER BY ${pageInfo.sort} ${pageInfo.order}
