mapper.select:
SELECT navigation_bar_id AS navigationBarId, navigation_id AS navigationId, name, target, sort, href, status, create_time AS createTime, create_by AS createBy, update_time AS updateTime, update_by AS updateBy, remarks FROM os_navigation_bar
navigation_id = #{navigationId}
AND ( target LIKE '%' #{search} '%' OR href LIKE '%' #{search} '%' OR name LIKE '%' #{search} '%' OR remarks LIKE '%' #{search} '%' OR create_by LIKE '%' #{search} '%' OR update_by LIKE '%' #{search} '%' )
ORDER BY ${pageInfo.sort} ${pageInfo.order}
