mapper.select:
    	SELECT
    		category_id AS categoryId
		FROM
			os_category
		WHERE
			status = #{status}
		AND parent_id IN (
			SELECT
				category_id AS categoryId
			FROM
				os_category
			WHERE
				STATUS = #{status}
			AND parent_id = #{categoryId}
		)
		OR parent_id = #{categoryId}
		OR category_id = #{categoryId}

    	SELECT
    		category_id AS categoryId,
			name
		FROM
			os_category
		WHERE
			status = #{status}
		AND show_in_nav = #{showInNav}
		AND type = #{type}
		ORDER BY
			sort
		LIMIT ${navNumber}

SELECT
FROM os_category WHERE parent_id = #{parentId}
AND ( name LIKE '%' #{search} '%' OR page_title LIKE '%' #{search} '%' OR page_description LIKE '%' #{search} '%' OR page_keyword LIKE '%' #{search} '%' OR remarks LIKE '%' #{search} '%' )
ORDER BY ${pageInfo.sort} ${pageInfo.order}
