SELECT p.product_id AS productId, product_number AS productNumber, name, show_price AS showPrice, introduce, pic_img AS picImg, comment_number AS commentNumber, label_name AS labelName FROM os_product_category c LEFT JOIN os_product p ON c.product_id = p.product_id LEFT JOIN os_product_attribute a ON a.product_id = p.product_id LEFT JOIN ( SELECT label_id, label_name FROM os_label WHERE status = #{status} ) l ON p.label_id = l.label_id WHERE c.category_id IN
#{categoryId}
AND p.show_in_shelve = #{status} AND p.show_in_top = #{showInTop} ORDER BY rand() LIMIT ${showNumber}

