SELECT p.product_id AS productId, product_number AS productNumber, name, show_price AS showPrice, introduce, pic_img AS picImg, label_name AS labelName FROM os_product p LEFT JOIN os_product_category c ON c.product_id = p.product_id LEFT JOIN os_label l ON l.label_id = p.label_id LEFT JOIN os_product_attribute a ON a.product_id = p.product_id 1=1
AND p.show_in_shelve = #{status}
AND name LIKE '%' #{search} '%' OR introduce LIKE '%' #{search} '%' OR search_key LIKE '%' #{search} '%' OR page_title LIKE '%' #{search} '%' OR page_description LIKE '%' #{search} '%' OR page_keyword LIKE '%' #{search} '%'
ORDER BY ${pageInfo.sort} ${pageInfo.order}
