mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tb_item_cat (parent_id, name, status, 
  sort_order, is_parent, created, updated
  )
values (#{parentId,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{sortOrder,jdbcType=INTEGER}, #{isParent,jdbcType=BIT}, #{created,jdbcType=TIMESTAMP}, #{updated,jdbcType=TIMESTAMP}
  )

mapper.select:
select * from tb_item_cat
where parent_id = #{parentId}
order by sort_order
