mapper.insert:
insert into tb_item_desc (item_id, created, updated, item_desc
  )
values (#{itemId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}, #{updated,jdbcType=TIMESTAMP}, #{itemDesc,jdbcType=LONGVARCHAR}
  )

mapper.update:
update tb_item_desc
set created = #{created,jdbcType=TIMESTAMP},
  updated = #{updated,jdbcType=TIMESTAMP},
  item_desc = #{itemDesc,jdbcType=LONGVARCHAR}
where item_id = #{itemId,jdbcType=BIGINT}

mapper.select:
select item_id, created, updated, item_desc
from tb_item_desc
where item_id = #{itemId,jdbcType=BIGINT}
