mapper.select:
SELECT count(*) FROM `users` WHERE `dtype` = 'Business'
AND created_date >= #{beginDate}
AND created_date <= #{endDate}

mapper.update:
   		UPDATE users
		SET last_modified_date = #{business.lastModifiedDate},
			version = #{business.version},
			balance = #{business.balance}
		WHERE
			`dtype` = 'Member'
		AND id = #{business.id}
		AND version = #{business.version}
