mapper.select:
SELECT ord.*,u.name uname,ad.accept accept,od.paystatus paystatus,od.sendstatus sendStatus FROM shop_order ord, shop_orderdetail od, shop_address ad, shop_user u WHERE ord.userid=u.ID AND ord.addressid=ad.id AND od.orderid=ord.id
AND ord.status=#{order.status}
AND od.paystatus=#{order.orderDetail.payStatus}
AND od.sendstatus=#{order.orderDetail.sendStatus}
limit #{indexPage} ,#{pageSize}

